var JoyStick = function (pjs, lPanelW, lPanelH) {
	'use strict';

	var log    = pjs.system.log;     // log = console.log;
	var game   = pjs.game;           // Game Manager
	var point  = pjs.vector.point;   // Constructor for Point
	var camera = pjs.camera;         // Camera Manager
	var brush  = pjs.brush;          // Brush, used for simple drawing
	var OOP    = pjs.OOP;            // Object's manager
	var math   = pjs.math;           // More Math-methods
	var system = pjs.system;

	var touch = pjs.touchControl.initTouchControl();

	var width  = game.getWH().w; // width of scene viewport
	var height = game.getWH().h; // height of scene viewport

	var btn = {
		up    : 'data:image/png;base64,',
		left  : 'data:image/png;base64,',
		down  : 'data:image/png;base64,',
		right : 'data:image/png;base64,'
	};

	var arrowWidth = lPanelW || 150;
	var arrowHeight = lPanelH || 120;

	var lPanel = system.newDOM('div');
	lPanel.style.backgroundColor = '';
	lPanel.innerHTML = '<table cellspacing="0" cellpadding="0" style="width: '+arrowWidth+'px; height: '+arrowHeight+'px;">'+
	'<tr><td></td><td align="center" valign="middle"><img id="btnUp" src="'+btn.up+'" style="width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td></tr>'+
	'<tr><td align="center" valign="middle"><img id="btnLeft" src="'+btn.left+'" style="width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td><td align="center" valign="middle"><img id="btnRight" src="'+btn.right+'" style="width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td></tr>'+
	'<tr><td></td><td align="center" valign="middle"><img id="btnDown" src="'+btn.down+'" style="width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td></tr>'+
	'</table>';

	lPanel.style.top = height - arrowHeight - 20 + 'px';
	lPanel.style.left = 10 + 'px';

	lPanel.style.display = 'none';

	var btnUp, btnDown, btnLeft, btnRight;

	var events = {
		onUpDown : false,
		onUpUp : false,

		onLeftDown : false,
		onLeftUp : false,

		onRightDown : false,
		onRightUp : false,

		onDownDown : false,
		onDownUp : false
	};

	var evtTypes = {
		'btnUp:press' : 'onUpDown',
		'btnUp:up' : 'onUpUp',

		'btnDown:press' : 'onDownDown',
		'btnDown:up' : 'onDownUp',

		'btnLeft:press' : 'onLeftDown',
		'btnLeft:up' : 'onLeftUp',

		'btnRight:press' : 'onRightDown',
		'btnRight:up' : 'onRightUp'
	};

	var pressed = {
		btnUp : false,
		btnDown : false,
		btnLeft : false,
		btnRight : false
	};

	this.isDown = function (key) {
		return pressed[key];
	};

	this.addEvent = function (evt, func) {
		events[evtTypes[evt]] = func;
	};

	this.show = function () {
		lPanel.style.display = 'block';
	};

	this.hide = function () {
		lPanel.style.display = 'none';
	};

	system.addEvent('onload', 'insertButtonJoyStick', function () {
		btnUp = document.getElementById('btnUp');
		btnLeft = document.getElementById('btnLeft');
		btnDown = document.getElementById('btnDown');
		btnRight = document.getElementById('btnRight');

		btnUp.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnUp = true; if (events.onUpDown) events.onUpDown(); });
		btnUp.addEventListener('touchend', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnUp = false; if (events.onUpUp) events.onUpUp(); });

		btnDown.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnDown = true; if (events.onDownDown) events.onDownDown(); });
		btnDown.addEventListener('touchend', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnDown = false; if (events.onDownUp) events.onDownUp(); });

		btnLeft.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnLeft = true; if (events.onLeftDown) events.onLeftDown(); });
		btnLeft.addEventListener('touchend', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnLeft = false; if (events.onLeftUp) events.onLeftUp(); });

		btnRight.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnRight = true; if (events.onRightDown) events.onRightDown(); });
		btnRight.addEventListener('touchend', function (e) { e.stopPropagation(); e.preventDefault(); pressed.btnRight = false; if (events.onRightUp) events.onRightUp(); });
	});

};