var JoyStick = function (pjs, controlType, lControlW, lControlH, rControlW, rControlH, lOffsetX, lOffsetY, rOffsetX, rOffsetY) {
	'use strict';

	var log    = pjs.system.log;     // log = console.log;
	var game   = pjs.game;           // Game Manager
	var point  = pjs.vector.point;   // Constructor for Point
	var camera = pjs.camera;         // Camera Manager
	var brush  = pjs.brush;          // Brush, used for simple drawing
	var OOP    = pjs.OOP;            // Object's manager
	var math   = pjs.math;           // More Math-methods
	var system = pjs.system;
	var vector = pjs.vector;

	var touch = pjs.touchControl.initTouchControl();

	var width  = game.getWH().w; // width of scene viewport
	var height = game.getWH().h; // height of scene viewport

	var onload = [], loaded = false;

	if (!controlType) controlType = 'arrows';

	var btn = {
		up    : 'data:image/png;base64,',
		left  : 'data:image/png;base64,',
		down  : 'data:image/png;base64,',
		right : 'data:image/png;base64,',
		fire  : 'data:image/png;base64,',
		stick : 'data:image/png;base64,'
	};

	var arrowWidth = lControlW || 150;
	var arrowHeight = lControlH || 120;
	var fireWidth = rControlW || 100;
	var fireHeight = rControlH || 100;
	var opacityMin = 0.8;
	var opacityMax = 1;

	lOffsetX = lOffsetX || 0;
	lOffsetY = lOffsetY || 0;
	rOffsetX = rOffsetX || 0;
	rOffsetY = rOffsetY || 0;

	var lControl = system.newDOM('div');

	if (controlType == 'arrows') {
		lControl.innerHTML = '<table cellspacing="0" cellpadding="0" style="opacity: '+opacityMin+'; width: '+arrowWidth+'px; height: '+arrowHeight+'px;">'+
		'<tr><td></td><td align="center" valign="middle"><img id="btnUp" src="'+btn.up+'" style="opacity: '+opacityMin+'; width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td></tr>'+
		'<tr><td align="center" valign="middle"><img id="btnLeft" src="'+btn.left+'" style="opacity: '+opacityMin+'; width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td><td align="center" valign="middle"><img id="btnRight" src="'+btn.right+'" style="opacity: '+opacityMin+'; width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td></tr>'+
		'<tr><td></td><td align="center" valign="middle"><img id="btnDown" src="'+btn.down+'" style="opacity: '+opacityMin+'; width: '+(arrowWidth/3)+'px; height: '+(arrowHeight/3)+'px;"/></td><td></td></tr>'+
		'</table>';
	} else if (controlType == 'stick') {
		lControl.innerHTML = '<img id="btnStick" src="'+btn.stick+'" style="opacity: '+opacityMin+'; height: '+arrowHeight+'px; width: '+arrowWidth+'px;">';
	}


	lControl.style.top  = lOffsetY + (height - arrowHeight - 20) + 'px';
	lControl.style.left = lOffsetX + 10 + 'px';

	var rControl = system.newDOM('div');
	rControl.innerHTML = '<img id="btnFire" src="'+btn.fire+'" style="opacity: '+opacityMin+'; width: '+fireWidth+'px; height: '+fireHeight+'px;">';
	rControl.style.top  = rOffsetY + (height - fireHeight - 20) + 'px';
	rControl.style.left = rOffsetX + (width - fireWidth - 20) + 'px';

	lControl.style.display = 'none';
	rControl.style.display = 'none';

	var btnUp, btnDown, btnLeft, btnRight, btnFire, btnStick;

	var stickData = {
		angle : 0,
		distance : point(0, 0),
		coord : point(0, 0),
		speed : point(0, 0),
		oldCoord : point(0, 0)
	};

	var centerPoint = point(lOffsetX + (10 + arrowWidth / 2), lOffsetY + (height - arrowHeight / 2 - 20));
	var updateTouchData = function (pnt) {
		stickData.oldCoord.x = stickData.coord.x;
		stickData.oldCoord.y = stickData.coord.y;
		stickData.coord.x = -centerPoint.x + pnt.x;
		stickData.coord.y = -centerPoint.y + pnt.y;
		stickData.angle = vector.getAngle2Points(centerPoint, pnt);
		stickData.distance = vector.getDistance(centerPoint, pnt);
	};

	var events = {
		onUpPress : false,
		onUpUp : false,
		onUpDown : false,

		onLeftPress : false,
		onLeftUp : false,
		onLeftDown : false,

		onRightPress : false,
		onRightUp : false,
		onRightDown : false,

		onDownPress : false,
		onDownUp : false,
		onDownDown : false,

		onStickPress : false,
		onStickUp : false,
		onStickDown : false,

		onFirePress : false,
		onFireUp : false,
		onFireDown : false
	};

	var evtTypes = {
		'btnUp:press' : 'onUpPress',
		'btnUp:up'    : 'onUpUp',
		'btnUp:down'  : 'onUpDown',

		'btnDown:press' : 'onDownPress',
		'btnDown:up'    : 'onDownUp',
		'btnDown:down'  : 'onDownDown',

		'btnLeft:press' : 'onLeftPress',
		'btnLeft:up'    : 'onLeftUp',
		'btnLeft:down'  : 'onLeftDown',

		'btnRight:press' : 'onRightPress',
		'btnRight:up'    : 'onRightUp',
		'btnRight:down'  : 'onRightDown',

		'btnStick:press' : 'onStickPress',
		'btnStick:up'    : 'onStickUp',
		'btnStick:down'  : 'onStickDown',

		'btnFire:press' : 'onFirePress',
		'btnFire:up'    : 'onFireUp',
		'btnFire:down'  : 'onFireDown'
	};

	var pressed = {
		btnUp : false,
		btnDown : false,
		btnLeft : false,
		btnRight : false,
		btnStick : false,
		btnFire : false
	};

	this.isDown = function (key) {
		return pressed[key];
	};

	this.on = function (evt, func) {
		events[evtTypes[evt]] = func;
	};

	this.show = function (lr) {
		if (!lr || lr == 'lControl') lControl.style.display = 'block';
		if (!lr || lr == 'rControl') rControl.style.display = 'block';
	};

	this.hide = function (lr) {
		if (!lr || lr == 'lControl') lControl.style.display = 'none';
		if (!lr || lr == 'rControl') rControl.style.display = 'none';
	};

	this.setImage = function (id, img) {
		var sm = function () {
			try {
				var el = document.getElementById(id);
				if (el) el.src = img;
			} catch (e) { }
		};
		if (loaded) sm();
		else onload.push(sm);
	};

	this.hideBtn = function (list) {
		var _hide = function () {
			list = list.split(',');

			OOP.forArr(list, function (el) {
				var btn = document.getElementById(el.trim());
				if (btn) {
					btn.style.display = 'none';
				}
			});
		};

		if (loaded) _hide();
		else onload.push(_hide);
	};

	this.showBtn = function (list) {
		var _hide = function () {
			list = list.split(',');

			OOP.forArr(list, function (el) {
				var btn = document.getElementById(el.trim());
				if (btn) {
					btn.style.display = 'block';
				}
			});
		};

		if (loaded) _hide();
		else onload.push(_hide);
	};

	system.addEvent('onload', 'insertButtonJoyStick', function () {

		loaded = true;

		OOP.forArr(onload, function (f) {
			f();
		});

		if (controlType == 'arrows') {
			btnUp = document.getElementById('btnUp');
			btnLeft = document.getElementById('btnLeft');
			btnDown = document.getElementById('btnDown');
			btnRight = document.getElementById('btnRight');
		} else if (controlType == 'stick') {
			btnStick = document.getElementById('btnStick');
		}

		btnFire = document.getElementById('btnFire');

		if (controlType == 'arrows') {
			btnUp.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMax; pressed.btnUp = true; if (events.onUpPress) events.onUpPress(); });
			btnUp.addEventListener('touchend',   function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMin; pressed.btnUp = false; if (events.onUpUp) events.onUpUp(); });

			btnDown.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMax; pressed.btnDown = true; if (events.onDownPress) events.onDownPress(); });
			btnDown.addEventListener('touchend',   function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMin; pressed.btnDown = false; if (events.onDownUp) events.onDownUp(); });

			btnLeft.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMax; pressed.btnLeft = true; if (events.onLeftPress) events.onLeftPress(); });
			btnLeft.addEventListener('touchend',   function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMin; pressed.btnLeft = false; if (events.onLeftUp) events.onLeftUp(); });

			btnRight.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMax; pressed.btnRight = true; if (events.onRightPress) events.onRightPress(); });
			btnRight.addEventListener('touchend',   function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMin; pressed.btnRight = false; if (events.onRightUp) events.onRightUp(); });
		} else if (controlType == 'stick') {

			btnStick.addEventListener('touchstart', function (e) {
				e.stopPropagation();
				e.preventDefault();
				updateTouchData(point(e.targetTouches[0].pageX, e.targetTouches[0].pageY));
				this.style.opacity = opacityMax;
				pressed.btnStick = true;
				if (events.onStickPress) events.onStickPress(stickData.angle, stickData.distance, stickData.coord, stickData.speed);
			});

			btnStick.addEventListener('touchend',   function (e) {
				e.stopPropagation();
				e.preventDefault();
				this.style.opacity = opacityMin;
				pressed.btnStick = false;
				if (events.onStickUp) events.onStickUp(stickData.angle, stickData.distance, stickData.coord, stickData.speed);
			});

			btnStick.addEventListener('touchmove',   function (e) {
				e.stopPropagation();
				e.preventDefault();
				updateTouchData(point(e.targetTouches[0].pageX, e.targetTouches[0].pageY));
				stickData.speed.x = stickData.coord.x - stickData.oldCoord.x;
				stickData.speed.y = stickData.coord.y - stickData.oldCoord.y;
			 });
		}

		btnFire.addEventListener('touchstart', function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMax; pressed.btnFire = true; if (events.onFirePress) events.onFirePress(); });
		btnFire.addEventListener('touchend',   function (e) { e.stopPropagation(); e.preventDefault(); this.style.opacity = opacityMin; pressed.btnFire = false; if (events.onFireUp) events.onFireUp(); });

		system.addEvent('postLoop', 'onDownJoyStick', function () {
			if (events.onUpDown && pressed.btnUp) events.onUpDown();
			if (events.onDownDown && pressed.btnDown) events.onDownDown();
			if (events.onLeftDown && pressed.btnLeft) events.onLeftDown();
			if (events.onRightDown && pressed.btnRight) events.onRightDown();
			if (events.onStickDown && pressed.btnStick) events.onStickDown(stickData.angle, stickData.distance, stickData.coord, stickData.speed);
			if (events.onFireDown && pressed.btnFire) events.onFireDown();
			stickData.speed.x = stickData.speed.y = 0;
			stickData.oldCoord.x = stickData.coord.x;
			stickData.oldCoord.y = stickData.coord.y;
		});
	});
};